import React, { useState } from 'react'
import {v4 as uuidv4} from "uuid"

export default function Todos () {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);
  const [editTodo, seteditTodo] =useState(null)
  const onInputChange = (e) =>{
    setInput(e.target.value);
  }

  const onFormSubmit = (e)=>{
    e.preventDefault()
    setTodos([...todos,{id:uuidv4(), title:input,completed:false}])
    setInput("")
    console.log(todos);
  }

  const handleDelete =({id}) =>{
    setTodos(todos.filter((todo) => todo.id !== id ))
  }

  const handleEdit = ({id}) => {
    setTodos(todos.filter((todo)=> todo.id !== id))
  }
  return (
    <div>
      <h1>Todo-List</h1>
      <form onSubmit={onFormSubmit}>
        <input type="text" value={input} required onChange={onInputChange} className='border border-black'/>
        <button type="submit">Add</button>
      </form>
      <div>
        <h2>Todo-List </h2>
        {todos.map((todo)=>(
          <li key={todo.id}>
            <input type="text" value={todo.title} onChange={(e)=>e.preventDefault()} />
            <div>
          <button className='text-red-500 mr-5'>
            <i>add</i>
          </button>
          <button className='text-green-500 mr-5' onClick={() => handleEdit(todo)}>
            <i>edit</i>
          </button>
          <button className='text-yellow-500' onClick={()=>handleDelete(todo)}>
            <i>delete</i>
          </button>
        </div>
          </li>
          ))}
      </div>
    </div>
  )
}
