import React, { Component } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default class Rapot extends Component {
  state = {
    rapot:  [],
    buka: false,
    index: null,
  };

  pintu = (buka) => {
    this.setState({ buka, index: null });
  };

  tambahin = (event, p) => {
    event.preventDefault();
    const { rapot, index } = this.state;
    const { nama, nilai } = event.target;
    const newData = {
      nama: nama.value,
      nilai: nilai.value,
      predikat: nilai.value > 6 ? "lulus" : "tidak lulus",
      // keterangan:keterangan.value
    };
    if (index === null) {
      rapot.push(newData);
    } else {
      rapot[index] = newData;
    }
    console.log("value", event.target.nilai.value);
    console.log("etarget", event.target.nilai);
    this.setState({ rapot });
    this.pintu(false);
  };

  ganti =(e,i)=>{
    // const { rapot, index } = this.state;
    // console.log(rapot);
    const rapat = e.target.value
    // console.log([...rapot]);
    this.setState(prevState => {
      let rapot = { ...prevState.rapot };  // creating copy of state variable jasper
      rapot.nama = 'someothername';                     // update the name property, assign a new value                 
      return { rapot };                                 // return new object jasper object
    })

    // this.setState({ rapot : rapat });



  }

  hapusin = (x) => {
    const { rapot } = this.state;
    rapot.splice(x, 1);
    this.setState({ rapot });
  };

  editin = (i) => {
    this.setState({ buka: true, index: i });
  };

  componentDidUpdate() {
    // console.log("didupdate");
    // localStorage.setItem("rapot", JSON.stringify(this.state.rapot));
  }
  render() {
    // console.log("render");
    return (
      <div>
