            <Modal.Title>Masukin Nilainya Pak/Bu Guru</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={(event) => this.tambahin(event)}>
              <Form.Label>Nama</Form.Label>
              <Form.Control
                name="nama"
                defaultValue={
                  this.state.index !== null
                    ? this.state.rapot[this.state.index].nama
                    : null
                }
                type="text"
                placeholder="Nama Muridmu"
              />
              <Form.Text className="text-muted">
                nama tidak boleh salah sedikitpun.
              </Form.Text>
              <Form.Label>Nilai</Form.Label>

              <Form.Control
                name="nilai"
                defaultValue={
                  this.state.index !== null
                    ? this.state.rapot[this.state.index].nilai
                    : null
                }
                type="number"
