            <Button onClick={() => this.hapusin(i)}>Hapus</Button>
            <Button onClick={() => this.editin(i)}>Edit</Button>
            {/* <button onClick={() => this.tambahin(i)}>Cek</button> */}
          </div>
        ))}
      </div>
    );
  }
}
