import { useEffect, useState } from "react";
import { HiPlus } from "react-icons/hi";
import { MdDelete, MdSaveAlt } from "react-icons/md";

import axios from "axios";
import { IoMdArrowRoundForward, IoMdArrowRoundBack } from "react-icons/io";

const API_URL = "http://localhost:3004";
function Todo() {
  const [todo, gantitodo] = useState([
    { label: "minum kopi", status: "draft" },
  ]);
  const [edit, gantiedit] = useState("");

  const getData = async () => {
    // const response = await axios.get(`${API_URL}/posts`);
    // console.log(response, "response");
    // const data = 'http://localhost:3004/posts'
    axios
      .get(`${API_URL}/posts`)
      .then((r) => gantitodo(r.data))
      .catch((e) => console.log(e));
  };
  useEffect(() => {
    getData();
  }, []);



  const handleSubmit = (e) => {
    e.preventDefault();
    const label = e.target.label.value;
    // const newTodo = [...todo];
    // newTodo.push({ label, status: "draft" });
    // gantitodo(newTodo);
    const payload = { label, status: "draft" };
    axios
      .post(`${API_URL}/posts`, payload, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then(() => {
        getData();
      });
    e.target.label.value = "";
  };

  const checktodo = (e, i) => {
    const newTodo = [...todo];
    // if (e.target.checked) {
      const id = newTodo[i].id;
      const payload = { status: "done", time: Date.now() };
      axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
        getData();
      });
    // }
  };

  const checkdraft = (e, i) => {
    const newTodo = [...todo];
    // if (e.target.checked) {
      // newTodo[i].status = "draft";
      // gantitodo(newTodo);
      // console.log(newTodo[i].status);
      const id = newTodo[i].id;
      const payload = { status: "draft", time: Date.now() };
      axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
        getData();
      });
    // }
  };

  const handleOnChange = (e, i) => {
    const newTodo = [...todo];
    newTodo[i].label = e.target.value;
    gantiedit(e.target.value);
    const id = newTodo[i].id;
    console.log(id);
    // const payload = { label: e.target.value };

    // axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
    //   getData();
    // });

    // console.log(todo);
    // const { name, value } = e.target;
    // console.log({ ...todo, [name]: value });
    // gantitodo({ ...todo, [name]: value });
  };
  // console.log(edit,"coba");

  const handleEdit = (x, i) => {
    // const newTodo = [...todo];
    // const id = newTodo[i].id;
    // console.log(id,"2");
    // console.log(x.id, "id");
    if (edit) {
      const payload = { label: edit };

      axios.patch(`${API_URL}/posts/${x.id}`, payload).then(() => {
        getData();
      });
    }
  };

  const onDelete = (e, i) => {
    const newTodo = [...todo];
    const id = newTodo[i].id;
    // const payload ={status : "done"};
    axios.delete(`${API_URL}/posts/${id}`).then(() => {
      getData();
    });
  };

  // const sorted = [...todo];

  // sorted.sort((a, b) => {
  // console.log(`a ${a.sort}`,`b ${b.sort}`,a.sort>b.sort)
  // return b.sort-a.sort
  // return (a.sort > b.sort ? 1 : b.sort > a.sort ? -1 : 0)
  // });

  // console.log(sorted);

  const onDropTodo = (e) => {
    let value = JSON.parse(e.dataTransfer.getData("done"));
    const newTodo = [...todo];
    // newTodo[value.index].status = "draft";
    // gantitodo(newTodo);
    const id = newTodo[value.index].id;
    const payload = { status: "draft", time: Date.now() };

    axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
      getData();
    });

    console.log(newTodo[value.index].status, "newTodo[value.index]");
  };

  const onDragTodo = (e, x, i) => {
    e.dataTransfer.setData("draft", JSON.stringify({ ...x, index: i }));
  };

  const onDropDone = (e) => {
    let value = JSON.parse(e.dataTransfer.getData("draft"));
    const newTodo = [...todo];
    // newTodo[value.index].status = "done";
    // gantitodo(newTodo);
    const id = newTodo[value.index].id;
    const payload = { status: "done", time: Date.now() };
    axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
      getData();
    });
  };

  const onDragDone = (e, x, i) => {
    e.dataTransfer.setData("done", JSON.stringify({ ...x, index: i }));
  };

  return (
    <div>
      <h1 className="text-center font-extrabold text-4xl text-teal-500			">
        FAKE TRELLO
      </h1>
      <div className="grid grid-cols-2 gap-10">
        <div
          className="p-10 mx-auto w-full"
          onDrop={(e) => onDropTodo(e)}
          onDragOver={(e) => e.preventDefault()}
        >
          <div className="text-xl text-gray-500">Todo</div>
          <form className="space-y-5 m-5 w-full" onSubmit={handleSubmit}>
            {todo
              .sort((a, b) => (a.time > b.time ? 1 : b.time > a.time ? -1 : 0))
              .map((x, i) => {
                return x.status === "draft" ? (
                  <div
                    key={i}
                    draggable
                    onDragStart={(e) => onDragTodo(e, x, i)}
                    className="flex items-center space-x-3 border border-gray-300 px-4 py-2 rounded"
                  >
                    {/* <input
                      type="button"
                      value={}
                      onChange={(e) => checktodo(e, i)}
                      defaultChecked={false}
                    /> */}
                    <IoMdArrowRoundForward 
                      onClick={(e) => checktodo(e, i)}
                      role="button"
                      />
                    {/* <label htmlFor="">{x.label}</label> */}

                    <input
                      type="text"
                      onChange={(e) => handleOnChange(e, i)}
                      value={x.label}
                      className="w-full "
                    />
                    {/* pake cara ambil map dari item */}
                    <MdSaveAlt
                      onClick={() => handleEdit(x, i)}
                      className="hover:text-red-700"
                      role="button"
                    />
                    {/* Pake cara object asign */}
                    <MdDelete
                      onClick={(e) => onDelete(e, i)}
                      className="hover:text-red-700"
                      role="button"
                    />
                  </div>
                ) : null;
              })}
            <div className="flex items-center space-x-3 border border-gray-300 px-4 py-2 rounded">
              <HiPlus />
              <input
                ref={todoinputref}
                required
                autoComplete="off"
                name="label"
                type="text"
                className="w-full outline-0"
                placeholder="Write as You Like 🔪️"
                // onKeyUp={onEnter}
              />
              {/* dengan type submit bisa enter juga */}
              <button type="submit" className="hover:text-red-700">
                Save
              </button>
            </div>
          </form>
        </div>
        <div
          className="p-10 mx-auto w-full"
          onDrop={(e) => onDropDone(e)}
          onDragOver={(e) => e.preventDefault()}
        >
          <div className="text-xl text-gray-500">Done</div>
          <div className="space-y-5 m-5 w-full">
            {todo
              .sort((a, b) => (a.time > b.time ? 1 : b.time > a.time ? -1 : 0))
              .map((x, i) => {
                return x.status === "done" ? (
                  <div
                    key={i}
                    draggable
                    onDragStart={(e) => onDragDone(e, x, i)}
                    className="flex items-center space-x-3 border   border-gray-300 px-4 py-2 rounded"
                  >
                    {/* <input type="button" value={IoArrowBackCircleSharp} onChange={(e) => checkdraft(e, i)} /> */}
                    <IoMdArrowRoundBack
                      onClick={(e) => checkdraft(e, i)}
                      role="button"
                      />
                    {/* <label htmlFor="">{x.label}</label> */}
                    <input
                      type="text"
                      onChange={(e) => handleOnChange(e, i)}
                      value={x.label}
                      className="w-full "
                    />

                    <MdSaveAlt
                      onClick={() => handleEdit(x, i)}
                      className="hover:text-red-700"
                      role="button"
                    />

                    <MdDelete
                      onClick={(e) => onDelete(e, i)}
                      className="hover:text-red-700"
                      role="button"
                    />
                  </div>
                ) : null;
              })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Todo;
// kenapa state edit berubah ketika di setState ke State lainnya
