import React, { useState } from "react";
import { useForm } from "react-hook-form";

export default function Form() {
  const {
    register,
    handleSubmit,
    reset,
    getValues,
    formState: { errors },
  } = useForm();
  const [data, setData] = useState();
  const [image, setImage] = useState(localStorage["fileBase64"] );
  const onSubmit = (data) => {
    console.log(data);
    setData(getValues());
    alert("Input Tim Berhasil ðŸ”ªï¸ ");
    reset();
  };

  const imageUpload = (e) => {
    const file = e.target.files[0];
    console.log(file,"file");
    getBase64(file)
      .then((base64) => {
        localStorage["fileBase64"] = base64;
        console.debug("file stored", base64);
      })
      .then(() => setImage(localStorage["fileBase64"]));
  };

  const getBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
      reader.readAsDataURL(file);
    });
  };

  return (
    <div className="container mx-auto px-6 max-w-7xl">
      <h1 className="text-center mb-5 text-red-600 ">Input Data Tim Sepakbola</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="max-w-3xl m-auto">
        <label>Nama Tim </label>

        <input type="text" {...register("tim", { required: true })} className="border border-black py-2.5 " />
        {/* required true, artinya dibutuhkan bener2 harus diisi */}
        {/* validasi berbentuk object seperti required dan lain lain ya adick adick */}

        {errors.tim && <p>Harus kamu isi ya adick-adick</p>}

        <label>Logo Tim</label>

        <input type="file" id="logo" name="logo" onChange={imageUpload} />

        <label>Tahun Berdiri</label>

        <input type="number" {...register("tahun", { min: 1999, max: 2025 })} />

        {errors.tahun && (
          <p className="error">Minimal tahun 1999 dan maximal tahun 2025</p>
        )}

        <label>Alamat Markas Tim</label>

        <input type="text" {...register("alamat")} />

        <label>Kota Markas Tim</label>

        <input type="text" {...register("kota", { pattern: /^[A-Za-z]+$/i })} />

        {errors.kota && <p>Satu kata aja ya adick-adick</p>}

        <button type="submit">Submit</button>
      </form>

      {data && (
        <div>
          <table className="border">
            <tr>
              <th>Tim</th>
              <th>Logo</th>
              <th>Tahun Berdiri</th>
              <th>Alamat Markas Tim</th>
              <th>Kota Markas Tim</th>
            </tr>
            <tr>
              <td>{data && data.tim}</td>
              <td>
              <img src={image} width="50" height="100" alt="embleh" /></td>
              <td> {data && data.tahun}</td>
              <td>{data && data.alamat}</td>
              <td>{data && data.kota}</td>
            </tr>
          </table>
        </div>
      )}
      {/* object tidak bisa ditampilkan langsung maka harus distringkan dulu */}
    </div>
  );
}
