import React, { useState } from "react";
import { useForm } from "react-hook-form";

export default function Form() {
  const {
    register,
    handleSubmit,
    reset,
    getValues,
    formState: { errors },
  } = useForm();
  const [data, setData] = useState();
  const [image, setImage] = useState(localStorage["fileBase64"] );
  const onSubmit = (data) => {
    console.log(data);
    setData(getValues());
    alert("Input Tim Berhasil ðŸ”ªï¸ ");
    reset();
  };

  const imageUpload = (e) => {
    const file = e.target.files[0];
    console.log(file,"file");
    getBase64(file)
      .then((base64) => {
        localStorage["fileBase64"] = base64;
        console.debug("file stored", base64);
      })
      .then(() => setImage(localStorage["fileBase64"]));
  };

  const getBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
      reader.readAsDataURL(file);
    });
  };

  return (
    <div className="container mx-auto px-6 max-w-7xl ">
      <h1 className="mt-10 font-extrabold text-4xl uppercase text-center mb-5 text-red-600 ">Input Data Tim Sepakbola</h1>
      <div className="max-w-4xl m-auto  bg-gray-200 rounded-md ">
      <form onSubmit={handleSubmit(onSubmit)} className="max-w-3xl m-auto ">
        <label>Nama Tim </label>

        <input type="text" {...register("tim", { required: true })} className="border border-solid box-border border-black py-1 px-3.5 w-full rounded mb-2.5 " />
        {/* required true, artinya dibutuhkan bener2 harus diisi */}
        {/* validasi berbentuk object seperti required dan lain lain ya adick adick */}

        {errors.tim && <p className="text-left text-xs text-red-700 ">Harus kamu isi ya adick-adick</p>}

        <label>Logo Tim</label>

        <input type="file" id="logo" name="logo" onChange={imageUpload} className="border border-solid box-border border-black py-1 px-3.5 w-full rounded mb-2.5 "/>

        <label>Tahun Berdiri</label>

        <input type="number" {...register("tahun", { min: 1999, max: 2025 })} className="border border-solid box-border border-black py-1 px-3.5 w-full rounded mb-2.5 "/>

        {errors.tahun && (
          <p className="text-left text-xs text-red-700">Minimal tahun 1999 dan maximal tahun 2025</p>
        )}

        <label>Alamat Markas Tim</label>

        <input type="text" {...register("alamat")} className="border border-solid box-border border-black py-1 px-3.5 w-full rounded mb-2.5 "/>

        <label>Kota Markas Tim</label>

        <input type="text" {...register("kota", { pattern: /^[A-Za-z]+$/i })} className="border border-solid box-border border-black py-1 px-3.5 w-full rounded mb-2.5 "/>

        {errors.kota && <p className="text-left text-xs text-red-700">Satu kata aja ya adick-adick</p>}

        <button type="submit" className="w-full rounded-md bg-green bg-green-500 py-2 my-2.5" >Submit</button>
      </form>
      </div>

      {data && (
          <table className="max-w-3xl mx-auto my-5">
            <tr className="border border-solid ">
              <th className="text-left p-4 border border-solid">Tim</th>
              <th className="text-left p-4 border border-solid">Logo</th>
              <th className="text-left p-4 border border-solid">Tahun Berdiri</th>
              <th className="text-left p-4 border border-solid">Alamat Markas Tim</th>
              <th className="text-left p-4 border border-solid">Kota Markas Tim</th>
            </tr>
            <tr className="border border-solid ">
              <td className="text-center p-4 border border-solid">{data && data.tim}</td>
              <td className="text-center p-4 border border-solid">
              <img src={image} width="50" height="100" alt="embleh" /></td>
              <td className="text-center p-4 border border-solid"> {data && data.tahun}</td>
              <td className="text-center p-4 border border-solid">{data && data.alamat}</td>
              <td className="text-center p-4 border border-solid">{data && data.kota}</td>
            </tr>
          </table>
      )}
      {/* object tidak bisa ditampilkan langsung maka harus distringkan dulu */}
    </div>
  );
}
