import React, { useState } from 'react'
import { useForm, Controller } from "react-hook-form";


export default function App() {
  const {
    register,
    handleSubmit,
    getValues,
    setValue,
    // formState: { errors },
  } = useForm();
  const [data, setData] = useState([]);
  const onSubmit = (values) => {
    const dataBaru = JSON.parse(JSON.stringify(data));
    dataBaru.push(values);
    setData(dataBaru);
    console.log(values);
    alert("Input Berhasil ğŸ”ªï¸ ");
  };


  return (
    <div className="container mx-auto px-6 max-w-7xl ">
      <h1 className="mt-10 font-extrabold text-4xl uppercase text-center mb-5 text-red-600 ">
        TODO LIST
      </h1>
    <div className="grid grid-cols-2">
      <div className='text-center'>
      <h2 >Todo List</h2>
      <form onSubmit={handleSubmit(onSubmit)} >
          <input
            type="text"
            {...register("tim", { required: true })}
            className="border border-black box-border  rounded "
          />
          <br/>
           <button
            type="submit"
            className="w-auto rounded-lg bg-green-500"
          >
            Submit
          </button>
          </form>
      </div>
      <div className='text-center'>
        <h2>Done</h2>
        <form onSubmit={handleSubmit(onSubmit)} >
          <input
            type="text"
            {...register("tim", { required: true })}
            className="border border-black box-border  rounded "
          />
          <br/>

           <button
            type="submit"
            className="w-auto rounded-lg bg-green-500"
          >
            Submit
          </button>
          </form>
      </div>
    </div>
    </div>
  )
}
