import { useEffect, useRef, useState } from "react";
import { HiPlus } from "react-icons/hi";
import { MdDelete, MdSaveAlt } from "react-icons/md";

import axios from "axios";
import { IoMdArrowRoundForward, IoMdArrowRoundBack } from "react-icons/io";

const API_URL = "http://localhost:4000";
function Falcon() {
  const todoinputref = useRef();
  const [todo, gantitodo] = useState([]);
  const [edit, gantiedit] = useState("");
  const [groups, gantiGroups] = useState([]);

  const getData = async () => {
    axios
      .get(`${API_URL}/posts`)
      .then((r) => gantitodo(r.data))
      .catch((e) => console.log(e));
    axios
      .get(`${API_URL}/groups`)
      .then((r) => gantiGroups(r.data))
      .catch((e) => console.log(e));
  };
  useEffect(() => {
    getData();
  }, []);

  const handleSubmit = (e, gname) => {
    e.preventDefault();
    const label = e.target.label.value;
    const payload = { label, group: gname };
    axios
      .post(`${API_URL}/posts`, payload, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then(() => {
        getData();
      });
    e.target.label.value = "";
  };

  const addGroup = (e) => {
    const name = todoinputref.current.value;
    const payload = { name };
    axios
      .post(`${API_URL}/groups`, payload, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then(() => {
        getData();
      });
    todoinputref.current.value = "";
  };

  const onEnter = function (e) {
    if (e.code === "Enter") {
      const name = e.target.value;
    const payload = { name };
    axios
      .post(`${API_URL}/groups`, payload, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then(() => {
        getData();
      });
    e.target.value = "";
    }
  };

  const handleOnChange = (e, i) => {
    const newTodo = [...todo];
    newTodo[i].label = e.target.value;
    gantiedit(e.target.value);
    const id = newTodo[i].id;
    console.log(id);
  };

  const handleEdit = (x, i) => {
    if (edit) {
      const payload = { label: edit };

      axios.patch(`${API_URL}/posts/${x.id}`, payload).then(() => {
        getData();
      });
    }
  };

  const onDelete = (e, i) => {
    const newTodo = [...todo];
    const id = newTodo[i].id;
    axios.delete(`${API_URL}/posts/${id}`).then(() => {
      getData();
    });
  };

  const onDropTodo = (e, gname) => {
    let value = JSON.parse(e.dataTransfer.getData("xdata"));
    const newTodo = [...todo];
    const id = newTodo[value.index].id;
    const payload = { group: gname, time: Date.now() };

    axios.patch(`${API_URL}/posts/${id}`, payload).then(() => {
      getData();
    });
  };

  const onDragTodo = (e, x, i) => {
    e.dataTransfer.setData("xdata", JSON.stringify({ ...x, index: i }));
  };

  return (
    <div>
      <h1 className="text-center font-extrabold text-4xl text-teal-500">
        FAKE TRELLO
      </h1>
      <div className="grid  justify-center mt-10 ">
      <h1 className="text-center font-extrabold text-2xl text-blue-800	">
        TAMBAH GROUP
      </h1>
      <div className="flex items-center space-x-3 border border-gray-300 px-4 py-2 rounded cursor-pointer focus-within:border-red-300">
                <HiPlus />
                <input
                ref={todoinputref}

                  required
                  autoComplete="off"
                  name="group"
                  type="text"
                  className="w-full outline-none cursor-pointer"
                  placeholder="Write as You Like ðŸ”ªï¸"
                onKeyUp={(e)=>{if(e.key === "Enter"){
                  addGroup(todoinputref.current.value)
                }}}

                />
                <button onClick={(e)=>addGroup(e,todoinputref.current.value)} className="hover:text-red-700">
                  Save
                </button>

              </div>

      </div>
      <div className="flex ">
        {groups?.map((g) => (
          <div
            className="p-10 mx-auto max-w-lg	"
            onDrop={(e) => onDropTodo(e, g.name)}
            onDragOver={(e) => e.preventDefault()}
          >
            <form
              className="space-y-5 m-5 w-full"
              onSubmit={(e) => handleSubmit(e, g.name)}
            >
              <div className="text-xl text-gray-500">{g.name}</div>
              {todo
                .sort((a, b) =>
                  a.time > b.time ? 1 : b.time > a.time ? -1 : 0
                )
                .map((x, i) => {
                  return x.group === g.name ? (
                    <div
                      key={i}
                      draggable
                      onDragStart={(e) => onDragTodo(e, x, i)}
                      className="flex items-center space-x-3 border border-gray-300 px-4 py-2 rounded cursor-pointer focus-within:border-red-300 w-full"
                    >
                      <IoMdArrowRoundForward
                        role="button"
                      />
                      <input
                        type="text"
                        onChange={(e) => handleOnChange(e, i)}
                        value={x.label}
                        className="w-full outline-none cursor-pointer"
                      />
                      <MdSaveAlt
                        onClick={() => handleEdit(x, i)}
                        className="hover:text-red-700"
                        role="button"
                      />
                      <MdDelete
                        onClick={(e) => onDelete(e, i)}
                        className="hover:text-red-700"
                        role="button"
                      />
                    </div>
                  ) : null;
                })}
              <div className="flex items-center space-x-3 border border-gray-300 px-4 py-2 rounded cursor-pointer focus-within:border-red-300">
                <HiPlus />
                <input
                  required
                  autoComplete="off"
                  name="label"
                  type="text"
                  className="w-full outline-none cursor-pointer"
                  placeholder="Write as You Like ðŸ”ªï¸"
                />
                <button type="submit" className="hover:text-red-700">
                  Save
                </button>
              </div>
            </form>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Falcon;
// kenapa state edit berubah ketika di setState ke State lainnya
