import React, { useState } from 'react'
import { useForm, Controller } from "react-hook-form";


export default function App() {
  // const {
  //   register,
  //   handleSubmit,
  //   getValues,
  //   setValue,
    // formState: { errors },
  // } = useForm();
  const [todo, setTodo] = useState([]);
  const [done, setDone] = useState([]);

  // const onSubmit = (values) => {
  //   const dataBaru = JSON.parse(JSON.stringify(todo));
  //   dataBaru.push(values);
  //   setTodo(dataBaru);
  //   // alert("Input Berhasil 🔪️ ");
  //   console.log(values,"1");
  // };

  const onSubmit = (e) => {
    e.preventDefault()
    setTodo({ nama: event.target.halo.value })

    // setTodo(dataBaru);
    // alert("Input Berhasil 🔪️ ");
    // console.log(values,"1");
  };


  const pindah = (values) => {
    const dataBaru = JSON.parse(JSON.stringify(done));
    dataBaru.push(values);
    setDone(dataBaru);
    // alert("Pindah ke Done🔪️ ");
    console.log(values,"2");
  };

  const balik = (values) => {
    // const dataBaru = JSON.parse(JSON.stringify(todo));
    // dataBaru.push(values);
    // setDone(dataBaru);
    // alert("Pindah ke Done🔪️ ");
    console.log(values,"3");
  };
  console.log(done);
  console.log(todo);



  return (
    <div className="container mx-auto px-6 max-w-7xl ">
      <h1 className="mt-10 font-extrabold text-4xl uppercase text-center mb-5 text-red-600 ">
        TODO LIST
      </h1>
    <div className="grid grid-cols-2">
      <div className='text-center'>
      <h2 >Todo List</h2>

      {/* <form onSubmit={handleSubmit(onSubmit)} > */}
      <form onSubmit={(e) => onSubmit(e)}>

        {/* <label className='bg-red-500'>Tambah </label>
        <br/>
          <input
            type="text"
            {...register("todo")}
            className="border border-black box-border  rounded "
          /> */}
          <label className='bg-red-500'>Tambah </label>
        <br/>
          <input
            type="text"
            name="data"

            className="border border-black box-border  rounded "
          />
          <br/>
           <button
            className="w-auto rounded-lg bg-green-500"
          >
            Submit
          </button>


          </form>

          {todo.map((item,i) => (
        <div key={i} className='border border-black box-border'>
          <p>{item.todo}</p>
          <br/>

           <button
            onClick={()=>pindah(item.todo)}

            className="w-auto rounded-lg bg-green-500"
          >
            Pindah
          </button>
          </div>        
          ))}


      </div>
      <div className='text-center'>
        <h2>Done</h2>
        <form onSubmit={handleSubmit(onSubmit)} >
          <input
            type="text"
            {...register("done")}
            className="border border-black box-border  rounded "
          />
          <br/>

           <button
            type="submit"
            className="w-auto rounded-lg bg-green-500"
          >
            Submit
          </button>
          </form>

          {done.map((item,i) => (
        <div key={i} className='border border-black box-border'>
        <p>{item}</p>
        <button onClick={()=>balik(item)} className='bg-green-500 rounded'>Balik</button>
        </div>        
          ))}
      </div>
    </div>
    </div>
  )
}
