import React, { useState } from 'react'
import { useForm, Controller } from "react-hook-form";
import {v4 as uuidv4} from "uuid"



export default function App() {
  const {
    register,
    handleSubmit,
    getValues,
    setValue,
    reset
    // formState: { errors },
  } = useForm();
  const [todo, setTodo] = useState([]);
  const [done, setDone] = useState([]);
  const [editTodo, setEditTodo] = useState(null);

  const updateTodo = (todak,id) =>{
    const newTodo = todo.map((tod) => tod.id === id ? {todo,id} : tod)
    setTodo(newTodo)
    setEditTodo("")
  }

  const onSubmit = (values) => {
    if(!editTodo){
    const dataBaru = JSON.parse(JSON.stringify(todo));
    const obj =values
    obj.id =uuidv4()

    dataBaru.push(obj);
    setTodo(dataBaru);
    // alert("Input Berhasil 🔪️ ");
    // console.log(values,"1");
    console.log(dataBaru);
    reset()

  } else {
      updateTodo(values,editTodo.id)
    }
  };

  const pindah = (values,i) => {
    const dataBaru = JSON.parse(JSON.stringify(done));
    dataBaru.push(values);
    setDone(dataBaru);
    todo.splice(i, 1)
    // alert("Pindah ke Done🔪️ ");
    console.log(values,"2");

  };
  const hapusTodo = async (values,i) => {
    const data =JSON.parse(JSON.stringify(todo));
    await data.splice(i, 1)
    setTodo(data)
    // alert("Pindah ke Done🔪️ ");
    console.log(todo,"hapusTodo");

  };

  const balik = (values,i) => {
    const dataBaru = JSON.parse(JSON.stringify(todo));
    dataBaru.push(values);
    setTodo(dataBaru);
    // alert("Pindah ke Done🔪️ ");
    done.splice(i, 1)

    // console.log(dataBaru,"3");
  };

  const hapusDone =(values,i) =>{
    const data = JSON.parse(JSON.stringify(done))
    data.splice(i,1);
    setDone(data)
  }

  const handleEdit = ({id}) => {
    const findTodo = todo.find((todo)=> todo.id === id);
    setEditTodo(findTodo)
    // console.log(findTodo,"findTodo");
  }



  return (
    <div className="container mx-auto px-6 max-w-7xl ">
      <h1 className="mt-10 font-extrabold text-4xl uppercase text-center mb-5 text-red-600 ">
        TODO LIST
      </h1>
    <div className="grid grid-cols-2">
      <div className='text-center'>
      <h2 >Todo List</h2>

      <form onSubmit={handleSubmit(onSubmit)} >
        <label className='bg-red-500'>Tambah </label>
        <br/>
          <input
            autoFocus
            type="text"
            {...register("todo")}
            className="border border-black box-border  rounded "
          />
          <br/>
           <button
            type="submit"
            className="w-auto rounded-lg bg-green-500"
          >
            Submit
          </button>


          </form>

          {todo.map((item,i) => (
        <div key={i} className='border border-black box-border'>
          <p>{item.todo}</p>
          <br/>

           <button
            onClick={()=>pindah(item,i)}

            className="w-auto rounded-lg bg-green-500"
          >
            Pindah
          </button>
          <button
            onClick={()=>hapusTodo(item,i)}

            className="w-auto rounded-lg bg-red-500"
          >
            Hapus
          </button>
          <button
            onClick={()=>handleEdit(item)}

            className="w-auto rounded-lg bg-red-500"
          >
            Ganti
          </button>
          </div>        
          ))}


      </div>
      <div className='text-center'>
        <h2>Done</h2>


          {done.map((item,i) => (
        <div key={i} className='border border-black box-border'>
        <p>{item.todo}</p>
        <button onClick={()=>balik(item,i)} className='bg-green-500 rounded'>Balik</button>
        <button onClick={()=>hapusDone(item,i)} className='bg-green-500 rounded'>Hapus</button>
        </div>        
          ))}
      </div>
    </div>
    </div>
  )
}
