import React, { Component } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default class Rapot extends Component {
  state = {
    rapot:  [],
    buka: false,
    index: null,
  };

  pintu = (buka) => {
    this.setState({ buka, index: null });
  };

  tambahin = (event, p) => {
    event.preventDefault();
    const { rapot, index } = this.state;
    const { nama, nilai } = event.target;
    const newData = {
      nama: nama.value,
      nilai: nilai.value,
      predikat: nilai.value > 6 ? "lulus" : "tidak lulus",
      // keterangan:keterangan.value
    };
    if (index === null) {
      rapot.push(newData);
    } else {
      rapot[index] = newData;
    }
    console.log("value", event.target.nilai.value);
    console.log("etarget", event.target.nilai);
    this.setState({ rapot });
    this.pintu(false);
  };

  ganti =(e,i)=>{
    const { rapot, index } = this.state;
    // console.log(rapot);
    // const rapat = e.target.value
    console.log(rapot);
    // this.setState(prevState => {
    //   let rapot = { ...prevState.rapot }; 
    //   rapot.nama = 'someothername';                     
    //   return { rapot };                                 
    // })

    // this.setState({ rapot : rapat });



  }

  hapusin = (x) => {
    const { rapot } = this.state;
    rapot.splice(x, 1);
    this.setState({ rapot });
  };

  editin = (i) => {
    this.setState({ buka: true, index: i });
  };

  componentDidUpdate() {
    // console.log("didupdate");
    // localStorage.setItem("rapot", JSON.stringify(this.state.rapot));
  }
  render() {
    // console.log("render");
    return (
      <div>
        <h1>Rapot Kelas 12</h1>
        <Button onClick={() => this.pintu(true)}>Masukin</Button>
        <Modal
          animation={true}
          show={this.state.buka}
          onHide={() => this.pintu(false)}
        >
          <Modal.Header closeButton={() => this.pintu(false)}>
            <Modal.Title>Masukin Nilainya Pak/Bu Guru</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={(event) => this.tambahin(event)}>
              <Form.Label>Nama</Form.Label>
              <Form.Control
                name="nama"
                defaultValue={
                  this.state.index !== null
                    ? this.state.rapot[this.state.index].nama
                    : null
                }
                type="text"
                placeholder="Nama Muridmu"
              />
              <Form.Text className="text-muted">
                nama tidak boleh salah sedikitpun.
              </Form.Text>
              <Form.Label>Nilai</Form.Label>

              <Form.Control
                name="nilai"
                defaultValue={
                  this.state.index !== null
                    ? this.state.rapot[this.state.index].nilai
                    : null
                }
                type="number"
                max="10"
                min="0"
                placeholder="Masukin Nilai"
              />

              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Modal.Body>
        </Modal>
        {this.state.rapot.map((item, i) => (
          <div key={i}>
            <input
              type="text"
              defaultValue={item.nama}
              name="nama"
              onChange={(e) => this.ganti(e)}
            />
            {/* <input type="number"  
                name="nilai"

              defaultValue={item.nilai}
              onChange={(e) => this.ganti(e)} /> */}
            {/* <input type="text" defaultValue={item.predikat}
              onChange={(e) => this.ganti(e)} /> */}

            {/* <p>Nama: {item.nama}</p> */}
            {/* <p>Nilai: {item.nilai}</p> */}
            {/* <p>{this.tambahin}</p> */}
            <p>Predikat: {item.predikat}</p>

            <Button onClick={() => this.hapusin(i)}>Hapus</Button>
            <Button onClick={() => this.editin(i)}>Edit</Button>
            {/* <button onClick={() => this.tambahin(i)}>Cek</button> */}
          </div>
        ))}
      </div>
    );
  }
}
